  //CHECK STRING RFC
  public function is_rfc($rfc){
    $rfc = trim($rfc);
    $len = strlen($rfc);
    if($len >= 10 && $len <= 13){
      if($len == 13)$regex = '/^[A-Za-zÑñ&]{4}([0-9]{2})(1[0-2]|0[1-9])([0-3][0-9])([A-Z0-9]{3})$/';
      else $regex = '/^[A-Za-zÑñ&]{3}([0-9]{2})(1[0-2]|0[1-9])([0-3][0-9])([A-Z0-9]{3})$/';
      return preg_match($regex, $rfc);
    }else return FALSE;
  }

