  //CURL
  public function curl($args = []){
    $response = '';
    $url = $this->key('url', $args);

    if($url != ''){
      $data = $this->key('data', $args);
      $credentials = $this->key('credentials', $args);
      $timeout = $this->key('timeout', $args, 60);
      $max_redirs = $this->key('max_redirs', $args, 3);
      $port = $this->key('port', $args);
      $agent = $this->key('agent', $args);
      $method = $this->key('method', $args, 'GET');
      $follow = $this->key('follow', $args, FALSE);
      $header = $this->key('header', $args, []);
      $post_fields = $this->key('post_fields', $args, []);

    	$ch = curl_init($url);
    	curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, FALSE);
    	curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, FALSE);
   	  curl_setopt($ch, CURLOPT_SSLVERSION, CURL_SSLVERSION_TLSv1_2);
    	curl_setopt($ch, CURLOPT_RETURNTRANSFER, TRUE);

      //FOLLOW
      curl_setopt($ch, CURLOPT_FOLLOWLOCATION, $follow);

      //PORT
      if(is_numeric($port))curl_setopt($ch, CURLOPT_PORT, $port);

      //DATA
      if($this->is_content($data)){
    		$post_fields = http_build_query($data, '', '&');
    		curl_setopt($ch, CURLOPT_POST, TRUE);
        curl_setopt($ch, CURLOPT_HEADER, FALSE);
    	  $header[] = 'Content-length:'.strlen($post_fields);
      }

      //POSTFIELDS
      if($post_fields != '')curl_setopt($ch, CURLOPT_POSTFIELDS, $post_fields);

      //METHOD
      curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);

      //HEADER
      if($this->is_content($header)){
        curl_setopt($ch, CURLOPT_HTTPHEADER, $header);
      }

      //AGENT
      if($agent != ''){
        curl_setopt($ch, CURLOPT_USERAGENT, $agent);
      }

     	//TIMEOUT
    	if(is_numeric($timeout)){
    	  curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $timeout);
        curl_setopt($ch, CURLOPT_TIMEOUT, $timeout);

    	}

      //MAXREDIRS
      if(is_numeric($max_redirs)){
        curl_setopt($ch, CURLOPT_MAXREDIRS, $max_redirs);
      }

      //BASIC AUTH
      if($this->is_content($credentials)){
        if(array_key_exists('user', $credentials) && array_key_exists('pass', $credentials)){
    	    curl_setopt($ch, CURLOPT_USERPWD, "{$credentials['user']}:{$credentials['pass']}");
        }
      }

      //RESPONSE
      $response = curl_exec($ch);
      if(curl_errno($ch)){
        $code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $response = "Error ({$code}): ".curl_error($ch);
      }

      curl_close($ch);
    }
    return $response;
  }

